components/amp/css/footer:
.amp-footer {
    background: {{footer-background}};
    color: {{footer-text}};
    text-align: center;
    border-top: 1px solid #e8e8e8;
    overflow: hidden;
}

.amp-footer a {
    color: {{footer-link}};
    text-decoration: none
}
.amp-footer a:hover {
    color: {{footer-link}}
}

.amp-footer-heading {
    color: {{footer-heading}};
    text-transform: capitalize;
}

.amp-footer-list {
    margin: 0;
    list-style-type: none;
}

.amp-footer-list li {
    padding: 3px 0;
}
