components/products/card:
{{inject "isProductCardPresented" true}}

<article
    class="card
    {{#if alternate}} card--alternate{{/if}}
    {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}} has-sale-price{{/or}}"
    {{#if settings.data_tag_enabled}}
        data-event-type="{{event}}"
        data-entity-id="{{id}}"
        data-position="{{position}}"
        data-name="{{name}}"
        data-product-category="
        {{#each category}}
            {{#if @last}}
                {{this}}
            {{else}}
                {{this}}, 
            {{/if}}
        {{/each}}"
        data-product-brand="{{brand.name}}"
        data-product-price="
        {{#if price.with_tax}}
            {{price.with_tax.value}}
        {{else}}
            {{price.without_tax.value}}
        {{/if}}"
    {{/if}}>
    <figure class="card-figure">
        {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
            {{#if theme_settings.show_sale_badges}}
                <div class="sale-flag-side">
                    <span class="sale-text">Sale</span>
                </div>
            {{/if}}
        {{/or}}
        {{#if theme_settings.show-stock-badge}}
            {{#if stock_level}}
                {{#lte stock_level 5}}
                    <div class="stock-badge">
                        <span class="stock-message">Only {{stock_level}} left in stock</span>
                    </div>
                {{/lte}}
            {{/if}}
            {{#if out_of_stock_message}}
                <div class="stock-badge">
                    <span class="stock-message">{{{out_of_stock_message}}}</span>
                </div>
            {{/if}}
        {{/if}}

        {{#is stock_level 0}}
            {{#unless out_of_stock_message}}
                <div class="stock-badge">
                    <span class="stock-message">Out of stock</span>
                </div>
            {{/unless}}
        {{/is}}

        <a href="{{url}}" 
           class="card-figure__link"
           aria-label="{{name}},{{> components/products/product-aria-label}}"
           {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}
        >
            <div class="card-img-container">
                <img class="card-image lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}">
            </div>
        </a>
        {{#or theme_settings.show_product_quick_view show_cart_action show_compare}}
            <figcaption class="card-figcaption">
                <div class="card-figcaption-body">
                    <div class="card-figcaption--action-buttons">
                    {{#unless hide_product_quick_view}}
                        {{#if theme_settings.show_product_quick_view}}
                            {{#if settings.data_tag_enabled}}
                                <a class="button button--secondary button--small card-figcaption-button quickview" data-event-type="product-click" data-product-id="{{id}}">{{lang 'products.quick_view'}}</a>
                            {{else}}
                                <a class="button button--secondary button--small card-figcaption-button quickview" data-product-id="{{id}}">{{lang 'products.quick_view'}}</a>
                            {{/if}}
                        {{/if}}
                    {{/unless}}
                    {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                        {{#if show_cart_action}}
                            {{#if has_options}}
                                <a href="{{url}}" data-event-type="product-click" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                            {{/if}}
                            {{#if pre_order}}
                                <a href="{{pre_order_add_to_cart_url}}" data-event-type="product-click" class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                            {{/if}}
                            {{#if add_to_cart_url }}
                                <a href="{{add_to_cart_url}}" data-event-type="product-click" class="button button--small card-figcaption-button">{{lang 'products.add_to_cart'}}</a>
                            {{/if}}
                            {{#if out_of_stock_message }}
                                <a href="{{url}}" data-event-type="product-click" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                            {{/if}}
                        {{/if}}
                    {{/or}}
                    </div>
                    {{#if show_compare}}
                        <label class="card-compare" for="compare-{{id}}">
                            <input type="checkbox" class="compare-input" name="products[]" value="{{id}}" id="compare-{{id}}" data-compare-id="{{id}}"> <span style="display:none;">Remove from </span>{{lang 'products.compare'}}
                        </label>
                        <div class="compare-button-wrap">
                            <a class="compare-button" href="{{urls.compare}}" data-compare-nav>Compare <span class="countPill countPill--positive countPill--alt"></span> Items</a>
                        </div>
                    {{/if}}
                </div>
            </figcaption>
        {{/or}}
    </figure>
    <div class="card-body">
        <div>
            {{#if theme_settings.card-show-brand}}
                {{#if brand.name}}
                    <p class="card-text brand-name" data-test-info-type="brandName">{{brand.name}}</p>
                {{else}}
                    <p></p>
                {{/if}}
            {{/if}}
            <h4 class="card-title">
                <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>{{name}}</a>
            </h4>
            <div class="card-text" data-test-info-type="price">
                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                    {{> components/products/price price=price}}
                {{else}}
                    {{> components/common/login-for-pricing}}
                {{/or}}
            </div>
        </div>

        <div class="card-details">
            {{#and rating show_rating}}
                <p class="card-text" data-test-info-type="productRating">
                    <span class="rating--small">
                        {{> components/products/ratings rating=rating}}
                    </span>
                </p>
            {{/and}}
            
            {{> components/products/bulk-discount-rates}}
            {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
            <div class="card-text add-to-cart-button">
                    {{#if show_cart_action}}
                        {{#if has_options}}
                            <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                        {{/if}}
                        {{#if pre_order}}
                            <a href="{{url}}" class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                        {{/if}}
                        {{#if add_to_cart_url }}
                            <a href="{{add_to_cart_url}}" class="button button--small card-figcaption-button">{{lang 'products.add_to_cart'}}</a>
                        {{/if}}
                        {{#if out_of_stock_message }}
                            <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                        {{/if}}
                    {{/if}}

                    {{#if show_compare}}
                        <label class="button button--small card-figcaption-button compare" for="compare-{{id}}">
                          <span class="custom-checkbox"><input type="checkbox" name="products[]" value="{{id}}" id="compare-{{id}}" data-compare-id="{{id}}"></span>
                          <span class="compare-text">{{lang 'products.compare'}}</span>
                        </label>
                    {{/if}}
            </div>
            {{/or}}
        </div>
    </div>
</article>

components/products/product-aria-label:
{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
{{lang 'category.shop_by_price_range_aria'
       from=price.price_range.min.with_tax.formatted
       to=price.price_range.max.with_tax.formatted
}}
    {{else}}
{{lang 'category.shop_by_price_range_aria'
       from=price.price_range.min.without_tax.formatted
       to=price.price_range.max.without_tax.formatted
}}
    {{/and}}
{{else}}
    {{#if price.with_tax}}
        {{#if price.non_sale_price_with_tax}}
{{#if theme_settings.pdp-non-sale-price-label '===' 'Was:'}}{{lang "page_builder.pdp-non-sale-price-label"}}{{else}}{{theme_settings.pdp-non-sale-price-label}}{{/if}}{{price.non_sale_price_with_tax.formatted}},
{{#if theme_settings.pdp-sale-price-label '===' 'Now:'}}{{lang "page_builder.pdp-sale-price-label"}}{{else}}{{theme_settings.pdp-sale-price-label}}{{/if}}{{price.with_tax.formatted}}
        {{else}}
{{price.with_tax.formatted}}
        {{/if}}
    {{else}}
        {{#if price.non_sale_price_without_tax}}
{{#if theme_settings.pdp-non-sale-price-label '===' 'Was:'}}{{lang "page_builder.pdp-non-sale-price-label"}}{{else}}{{theme_settings.pdp-non-sale-price-label}}{{/if}}{{price.non_sale_price_without_tax.formatted}},
{{#if theme_settings.pdp-sale-price-label '===' 'Now:'}}{{lang "page_builder.pdp-sale-price-label"}}{{else}}{{theme_settings.pdp-sale-price-label}}{{/if}}{{price.without_tax.formatted}}
        {{else}}
{{price.without_tax.formatted}}
        {{/if}}
    {{/if}}
{{/and}}

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price schema_org=schema_org}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span class="price-section price-section--withTax non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                <span data-product-non-sale-price-with-tax class="price price--non-sale">
                    {{price.non_sale_price_with_tax.formatted}}
                </span>
            </span>
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp"> {{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                	content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="url" content="{{product.url}}">
                <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.with_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="true">
                </div>
            {{/if}}
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                <span data-product-non-sale-price-without-tax class="price price--non-sale">
                    {{price.non_sale_price_without_tax.formatted}}
                </span>
            </span>
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                    content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="url" content="{{product.url}}">
                <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.without_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="false">
                </div>
            {{/if}}
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
         </div>
    {{/if}}
{{/and}}

components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
    <dt class="productView-info-bulkPricing-name">{{lang 'products.bulk_pricing.title'}}</dt>
    <dd class="productView-info-value">
        <p>{{lang 'products.bulk_pricing.instructions'}}</p>
        <ul>
            {{#each bulk_discount_rates}}
            <li>
                {{lang 'products.bulk_pricing.range' min=min max=max}}
                {{#if type '===' 'percent'}}
                {{lang 'products.bulk_pricing.percent' discount=discount.formatted}}
                {{else if type '===' 'fixed'}}
                {{lang 'products.bulk_pricing.fixed' discount=discount.formatted}}
                {{else if type '===' 'price'}}
                {{lang 'products.bulk_pricing.price' discount=discount.formatted}}
                {{/if}}
            </li>
            {{/each}}
        </ul>
    </dd>
{{/if}}

components/products/price-range:
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
    <div class="price-section price-section--withTax rrp-price--withTax">
        <span data-page-builder-key="pdp-retail-price-label" data-default-translation="{{lang "page_builder.pdp-retail-price-label"}}">
            {{theme_settings.pdp-retail-price-label}}
        </span>
        <span data-product-rrp-price-with-tax class="price price--rrp">{{price.retail_price_range.min.with_tax.formatted}} - {{price.retail_price_range.max.with_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax rrp-price--withTax" style="display: none;">
            <span data-page-builder-key="pdp-retail-price-label" data-default-translation="{{lang "page_builder.pdp-retail-price-label"}}">
                {{theme_settings.pdp-retail-price-label}}
            </span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withTax non-sale-price--withTax" style="display: none;">
        <span data-page-builder-key="pdp-non-sale-price-label" data-default-translation="{{lang "page_builder.pdp-non-sale-price-label"}}">
            {{theme_settings.pdp-non-sale-price-label}}
        </span>
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label"
              style="display: none;"
              data-page-builder-key="pdp-sale-price-label"
              data-default-translation="{{lang "page_builder.pdp-sale-price-label"}}"
        >
            {{theme_settings.pdp-sale-price-label}}
        </span>
        <span data-product-price-with-tax class="price price--withTax">{{price.price_range.min.with_tax.formatted}} - {{price.price_range.max.with_tax.formatted}}</span>
        {{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
        {{#if schema_org}}
            <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                    content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
            <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            <meta itemprop="url" content="{{product.url}}">
            <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                <meta itemprop="minPrice" content="{{price_range.min.with_tax.value}}"  />
                <meta itemprop="price" content="{{price_range.min.with_tax.value}}">
                <meta itemprop="maxPrice" content="{{price_range.max.with_tax.value}}"  />
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="true">
            </div>
        {{/if}}
    </div>
{{/and}}
{{#and price.retail_price_range.min.without_tax price.retail_price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">
        <span data-page-builder-key="pdp-retail-price-label" data-default-translation="{{lang "page_builder.pdp-retail-price-label"}}">
            {{theme_settings.pdp-retail-price-label}}
        </span>
        <span data-product-rrp-price-without-tax class="price price--rrp">{{price.retail_price_range.min.without_tax.formatted}} - {{price.retail_price_range.max.without_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
            <span data-page-builder-key="pdp-retail-price-label" data-default-translation="{{lang "page_builder.pdp-retail-price-label"}}">
                {{theme_settings.pdp-retail-price-label}}
            </span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
        <span data-page-builder-key="pdp-non-sale-price-label" data-default-translation="{{lang "page_builder.pdp-non-sale-price-label"}}">
            {{theme_settings.pdp-non-sale-price-label}}
        </span>
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax{{#and price_range.min.with_tax price_range.max.with_tax}} price-section--minor{{/and}}" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label"
              style="display: none;"
              data-page-builder-key="pdp-sale-price-label"
              data-default-translation="{{lang "page_builder.pdp-sale-price-label"}}"
        >
            {{theme_settings.pdp-sale-price-label}}
        </span>
        <span data-product-price-without-tax class="price price--withoutTax">{{price.price_range.min.without_tax.formatted}} - {{price.price_range.max.without_tax.formatted}}</span>
        {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
        {{#if schema_org}}
            <meta itemprop="availability" itemtype="https://schema.org/ItemAvailability"
                    content="https://schema.org/{{#if product.pre_order}}PreOrder{{else if product.out_of_stock}}OutOfStock{{else if product.can_purchase '===' false}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="https://schema.org/OfferItemCondition" content="https://schema.org/{{product.condition}}Condition">
            <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            <meta itemprop="url" content="{{product.url}}">
            <div itemprop="priceSpecification" itemscope itemtype="https://schema.org/PriceSpecification">
                <meta itemprop="minPrice" content="{{price.price_range.min.without_tax.value}}"  />
                <meta itemprop="price" content="{{price.price_range.min.without_tax.value}}">
                <meta itemprop="maxPrice" content="{{price.price_range.max.without_tax.value}}"  />
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="false">
            </div>
        {{/if}}
    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
     <div class="price-section price-section--saving price" style="display: none;">
            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
            <span data-product-price-saved class="price price--saving">
                {{price.saved.formatted}}
            </span>
            <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
     </div>
{{/if}}
