pages/search:
---
product_results:
    limit: {{theme_settings.searchpage_products_per_page}}
---
{{inject 'categoryTree' forms.search.category_options}}
{{inject 'searchProductsPerPage' theme_settings.searchpage_products_per_page}}
{{inject 'searchResultsCount' (lang 'search.results.count' count=result_count search_query=(sanitize forms.search.query))}}
{{#partial "head"}}
    {{#if pagination.product_results.previous}}
        <link rel="prev" href="{{pagination.product_results.previous}}">
    {{/if}}
    {{#if pagination.product_results.next}}
        <link rel="next" href="{{pagination.product_results.next}}">
    {{/if}}
{{/partial}}

{{#partial "page"}}

<div class="container">
    {{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
    <section class="nav">
        {{#if forms.search.query}}
            <div id="search-results-heading">
                {{>components/search/heading}}
            </div>
        {{/if}}
        <nav class="navBar navBar--sub search-nav">
            <span id="search-tabs-widget-description" class="aria-description--hidden">
                {{lang 'search.tabs_accesibility_hint'}}
            </span>
            <ul role="tablist" class="navBar-section account-navigation" data-search-page-tabs>
                <li role="presentation" class="navBar-item">
                    <a aria-describedby="search-tabs-widget-description" role="tab" aria-controls="product-listing-container" id="search-results-product-count" class="navBar-action" href="{{forms.search.product_url}}" data-product-results-toggle>
                        {{>components/search/product-count}}
                    </a>
                </li>
                <li role="presentation" class="navBar-item">
                    <a aria-describedby="search-tabs-widget-description" role="tab" aria-controls="search-results-content" id="search-results-content-count" class="navBar-action" href="{{forms.search.content_url}}" data-content-results-toggle>
                        {{>components/search/content-count}}
                    </a>
                </li>
                {{#unless product_results.faceted_search_enabled}}
                    <li class="navBar-item navBar-item--separate">
                        <a class="navBar-action toggleLink" data-collapsible="advanced-search-content" href="#" aria-controls="advanced-search-content" aria-expanded="false">
                            <span class="toggleLink-text toggleLink-text--on">
                                {{lang 'forms.search.hide'}}
                            </span>
                            <span class="toggleLink-text toggleLink-text--off">
                                {{lang 'forms.search.show'}}
                            </span>
                        </a>
                    </li>
                {{/unless}}
            </ul>
        </nav>
        {{#unless product_results.faceted_search_enabled}}
            {{>components/search/advanced-search}}
        {{/unless}}
    </section>
</div>

<section class="page">
    <div class="grid-bg">
        <div class="container">
            {{#if product_results.faceted_search_enabled}}
                {{#gt result_count 0}}
                    <aside class="page-sidebar{{#if forms.search.section '!=' 'product'}} u-hiddenVisually{{/if}}" id="faceted-search-container">
                        {{> components/faceted-search/index product_results}}
                    </aside>
                {{/gt}}
            {{/if}}

            <main class="page-content page-content--centered">
                {{#if forms.search.has_suggestions}}
                    <div class="panel panel--large">
                        <div class="panel-body">
                            {{#if forms.search.suggested_query}}
                                <div class="search-suggestion">
                                    {{lang 'forms.search.did_you_mean'}} <strong>{{forms.search.suggested_query.query}}</strong>
                                    <a href="{{forms.search.suggested_query.url}}" class="search-refine">{{lang 'forms.search.refine'}}</a>
                                </div>
        
                                {{#if result_count '===' 0}}
                                    <div class="search-suggestion">
                                        <p>
                                            {{lang 'forms.search.your_search_for'}}
                                            "<strong>{{{sanitize forms.search.query}}}</strong>"
                                            {{lang 'forms.search.no_match'}}
                                        </p>
                                    </div>
                                {{/if}}
                            {{/if}}
        
                            {{#if category_results.length}}
                                <div class="search-suggestion">
                                    <h5 class="suggestion-title">{{lang 'forms.search.categories'}}</h5>
                                    <ul class="category-suggestion-list">
        
                                        {{#each category_results}}
                                        <li class="category-suggestion">
        
                                            {{#each this}}
                                                <a href="{{url}}">{{name}}</a>
                                                {{#unless @last}} > {{/unless}}
                                            {{/each}}
        
                                        </li>
                                        {{/each}}
                                    </ul>
                                </div>
                            {{/if}}
        
                            {{#if brand_results.length}}
                                <div class="search-suggestion">
                                    <h5 class="suggestion-title">{{lang 'forms.search.brand'}}</h5>
                                    {{#each brand_results}}
                                        <a href="{{url}}">{{name}}</a>
                                        {{#unless @last}}, {{/unless}}
                                    {{/each}}
                                </div>
                            {{/if}}
        
                            {{#if result_count '===' 0}}
                                <div class="search-suggestion">
                                    <h5 class="suggestion-title">{{lang 'forms.search.suggestions.title'}}</h5>
                                    <ul>
                                        <li>{{lang 'forms.search.suggestions.line1'}}</li>
                                        <li>{{lang 'forms.search.suggestions.line2'}}</li>
                                        <li>{{lang 'forms.search.suggestions.line3'}}</li>
                                    </ul>
                                </div>
                            {{/if}}
                        </div>
                    </div>
                {{/if}}
        
                <p role="status"
                   aria-live="polite"
                   class="quickSearchResultsAriaMessage aria-description--hidden u-hidden"
                   data-search-aria-message
                >
                    {{{lang 'search.results.count' count=result_count search_query=(sanitize forms.search.query)}}}
                </p>
        
                <div role="tabpanel" id="search-results-content" aria-labelledby="search-results-content-count" {{#if forms.search.section '!=' 'content'}}class="u-hidden"{{/if}}>
                    {{> components/search/content-listing}}
                </div>
        
                <div role="tabpanel" id="product-listing-container" aria-labelledby="search-results-product-count" {{#if forms.search.section '!=' 'product'}}class="u-hidden"{{/if}}>
                    {{> components/search/product-listing}}
                </div>
        
                {{{region name="search_below_content"}}}
            </main>
        </div>
    </div>
</section>

{{/partial}}
{{> layout/base}}

components/search/content-listing:
{{#if content_results}}
    {{> components/search/content-sort-box sort=pagination.content_results.sort}}
    <ul>
        {{#each content_results}}
            <strong><a href="{{url}}">{{{title}}}</a></strong>({{type}})
            <p>{{{content}}}</p>
        {{/each}}
    </ul>
    {{> components/common/paginator pagination.content_results reload=true}}
{{/if}}

components/search/product-listing:
{{inject 'noCompareMessage' (lang 'compare.no_compare')}}

{{#if product_results.products.length}}
    {{> components/products/filter sort=pagination.product_results.sort}}

    <form action="{{urls.compare}}" method='POST' {{#if settings.data_tag_enabled}} data-list-name="Search Results" {{/if}} data-product-compare>
        {{#if theme_settings.product_list_display_mode '===' 'grid'}}
            {{#if settings.data_tag_enabled}}
                {{> components/products/grid products=product_results.products show_compare=product_results.show_compare event="list" theme_settings=theme_settings}}
            {{else}}
                {{> components/products/grid products=product_results.products show_compare=product_results.show_compare theme_settings=theme_settings}}
            {{/if}}
        {{else}}
            {{#if settings.data_tag_enabled}}
                {{> components/products/list products=product_results.products show_compare=product_results.show_compare event="list" theme_settings=theme_settings}}
             {{else}}
                {{> components/products/list products=product_results.products show_compare=product_results.show_compare theme_settings=theme_settings}}
            {{/if}}
        {{/if}}
    </form>
    
    {{> components/common/paginator pagination.product_results}}
{{/if}}

components/products/list:
{{inject "isProductListPresented" true}}

<ul class="productList">
    {{#each products}}
    <li class="product">
		{{>components/products/list-item show_compare=../show_compare show_rating=../settings.show_product_rating theme_settings=../theme_settings customer=../customer event=../event settings=../settings position=(add @index 1)}}
    </li>
    {{/each}}
</ul>

components/faceted-search/facets/hierarc:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content {{#unless ../start_collapsed }} is-open {{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" class="navList" data-facet="{{facet}}" data-has-more-results="{{ has_more_results }}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected }} is-selected{{/if}}{{#if has_sub_categories}} has-children{{/if}}"
                        rel="nofollow"
                        data-id="{{id}}"
                        data-faceted-search-facet>
                        {{{sanitize title}}}
                        {{#if ../show_product_counts}}
                            <span>({{count}})</span>
                        {{/if}}
                    </a>
                    {{#if children}}
                        {{> components/faceted-search/facets/hierarchy-children children}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{hyphenate facet}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

components/faceted-search/selected-facet:
{{#if this.items}}
    <div class="facetedSearch-refineFilters sidebarBlock">
        <h5 class="sidebarBlock-heading">
            {{lang 'search.faceted.selected.title'}}
        </h5>

        <ul class="inlineList inlineList--labels">
            {{#each items}}
                <li>
                    <a href="{{url}}"
                       class="facetLabel"
                       rel="nofollow"
                       data-faceted-search-facet
                    >
                        {{#if facet '===' 'rating'}}
                            {{lang 'search.faceted.selected.rating-label' rating=title}}
                        {{else}}
                            {{{sanitize title}}}
                        {{/if}}
    
                        <svg class="icon">
                            <use xlink:href="#icon-close" />
                        </svg>
                    </a>
                </li>
            {{/each}}
        </ul>

        {{#if remove_all_url}}
            <a href="{{ remove_all_url }}" data-faceted-search-facet>
                {{lang 'search.faceted.selected.clear-all'}}
            </a>
        {{/if}}
    </div>
{{/if}}

components/faceted-search/facets/multi:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{dashcase facet}}" class="accordion-content {{#unless ../start_collapsed }} is-open {{/unless}}">
        <ul id="facetedSearch-navList--{{dashcase facet}}" data-facet="{{facet}}" class="navList" data-has-more-results="{{ has_more_results }}">
            {{#each items}}
                <li class="navList-item">
                    <a
                        href="{{url}}"
                        class="navList-action navList-action--checkbox{{#if selected}} is-selected{{/if}}"
                        rel="nofollow"
                        data-faceted-search-facet>
                        {{{sanitize title}}}
                        {{#if ../show_product_counts}}
                            <span>({{count}})</span>
                        {{/if}}

                        <span class="navList-action-close" aria-hidden="true">
                            <svg class="icon">
                                <use xlink:href="#icon-close"/>
                            </svg>
                        </span>
                    </a>
                </li>
            {{/each}}
        </ul>

        {{#if show_more_toggle}}
            <a href="#facetedSearch-navList--{{hyphenate facet}}" role="button" class="toggleLink">
                <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
            </a>
        {{/if}}
    </div>
</div>
{{/if}}

components/faceted-search/facets/rating:
{{#if items}}
<div class="accordion-block">
    {{> components/faceted-search/faceted-search-navigation}}

    <div id="facetedSearch-content--{{hyphenate facet }}" class="accordion-content facetedSearch-content--{{hyphenate facet}} {{#unless ../start_collapsed }} is-open {{/unless}}">
        <ul class="navList">
            {{#each items}}
                <li class="navList-item">
                    <a href="{{url}}"
                       class="navList-action{{#if selected}} is-selected{{/if}}"
                       rel="nofollow"
                       data-faceted-search-facet>
                        <span class="rating--small">
                            {{> components/products/ratings rating=title}}
                        </span>
                        {{lang 'search.faceted.rating.and-up'}}
                        {{#if ../show_product_counts}}
                            <span>({{count}})</span>
                        {{/if}}
                    </a>
                </li>
            {{/each}}
        </ul>
    </div>
</div>
{{/if}}

components/products/card:
{{inject "isProductCardPresented" true}}

<article
    class="card
    {{#if alternate}} card--alternate{{/if}}
    {{#or price.non_sale_price_with_tax price.non_sale_price_without_tax}} has-sale-price{{/or}}"
    {{#if settings.data_tag_enabled}}
        data-event-type="{{event}}"
        data-entity-id="{{id}}"
        data-position="{{position}}"
        data-name="{{name}}"
        data-product-category="
        {{#each category}}
            {{#if @last}}
                {{this}}
            {{else}}
                {{this}}, 
            {{/if}}
        {{/each}}"
        data-product-brand="{{brand.name}}"
        data-product-price="
        {{#if price.with_tax}}
            {{price.with_tax.value}}
        {{else}}
            {{price.without_tax.value}}
        {{/if}}"
    {{/if}}>
    <figure class="card-figure">
        {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
            {{#if theme_settings.show_sale_badges}}
                <div class="sale-flag-side">
                    <span class="sale-text">Sale</span>
                </div>
            {{/if}}
        {{/or}}
        {{#if theme_settings.show-stock-badge}}
            {{#if stock_level}}
                {{#lte stock_level 5}}
                    <div class="stock-badge">
                        <span class="stock-message">Only {{stock_level}} left in stock</span>
                    </div>
                {{/lte}}
            {{/if}}
            {{#if out_of_stock_message}}
                <div class="stock-badge">
                    <span class="stock-message">{{{out_of_stock_message}}}</span>
                </div>
            {{/if}}
        {{/if}}

        {{#is stock_level 0}}
            {{#unless out_of_stock_message}}
                <div class="stock-badge">
                    <span class="stock-message">Out of stock</span>
                </div>
            {{/unless}}
        {{/is}}

        <a href="{{url}}" 
           class="card-figure__link"
           aria-label="{{name}},{{> components/products/product-aria-label}}"
           {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}
        >
            <div class="card-img-container">
                <img class="card-image lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}">
            </div>
        </a>
        {{#or theme_settings.show_product_quick_view show_cart_action show_compare}}
            <figcaption class="card-figcaption">
                <div class="card-figcaption-body">
                    <div class="card-figcaption--action-buttons">
                    {{#unless hide_product_quick_view}}
                        {{#if theme_settings.show_product_quick_view}}
                            {{#if settings.data_tag_enabled}}
                                <a class="button button--secondary button--small card-figcaption-button quickview" data-event-type="product-click" data-product-id="{{id}}">{{lang 'products.quick_view'}}</a>
                            {{else}}
                                <a class="button button--secondary button--small card-figcaption-button quickview" data-product-id="{{id}}">{{lang 'products.quick_view'}}</a>
                            {{/if}}
                        {{/if}}
                    {{/unless}}
                    {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                        {{#if show_cart_action}}
                            {{#if has_options}}
                                <a href="{{url}}" data-event-type="product-click" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                            {{/if}}
                            {{#if pre_order}}
                                <a href="{{pre_order_add_to_cart_url}}" data-event-type="product-click" class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                            {{/if}}
                            {{#if add_to_cart_url }}
                                <a href="{{add_to_cart_url}}" data-event-type="product-click" class="button button--small card-figcaption-button">{{lang 'products.add_to_cart'}}</a>
                            {{/if}}
                            {{#if out_of_stock_message }}
                                <a href="{{url}}" data-event-type="product-click" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                            {{/if}}
                        {{/if}}
                    {{/or}}
                    </div>
                    {{#if show_compare}}
                        <label class="card-compare" for="compare-{{id}}">
                            <input type="checkbox" class="compare-input" name="products[]" value="{{id}}" id="compare-{{id}}" data-compare-id="{{id}}"> <span style="display:none;">Remove from </span>{{lang 'products.compare'}}
                        </label>
                        <div class="compare-button-wrap">
                            <a class="compare-button" href="{{urls.compare}}" data-compare-nav>Compare <span class="countPill countPill--positive countPill--alt"></span> Items</a>
                        </div>
                    {{/if}}
                </div>
            </figcaption>
        {{/or}}
    </figure>
    <div class="card-body">
        <div>
            {{#if theme_settings.card-show-brand}}
                {{#if brand.name}}
                    <p class="card-text brand-name" data-test-info-type="brandName">{{brand.name}}</p>
                {{else}}
                    <p></p>
                {{/if}}
            {{/if}}
            <h4 class="card-title">
                <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>{{name}}</a>
            </h4>
            <div class="card-text" data-test-info-type="price">
                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                    {{> components/products/price price=price}}
                {{else}}
                    {{> components/common/login-for-pricing}}
                {{/or}}
            </div>
        </div>

        <div class="card-details">
            {{#and rating show_rating}}
                <p class="card-text" data-test-info-type="productRating">
                    <span class="rating--small">
                        {{> components/products/ratings rating=rating}}
                    </span>
                </p>
            {{/and}}
            
            {{> components/products/bulk-discount-rates}}
            {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
            <div class="card-text add-to-cart-button">
                    {{#if show_cart_action}}
                        {{#if has_options}}
                            <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                        {{/if}}
                        {{#if pre_order}}
                            <a href="{{url}}" class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                        {{/if}}
                        {{#if add_to_cart_url }}
                            <a href="{{add_to_cart_url}}" class="button button--small card-figcaption-button">{{lang 'products.add_to_cart'}}</a>
                        {{/if}}
                        {{#if out_of_stock_message }}
                            <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                        {{/if}}
                    {{/if}}

                    {{#if show_compare}}
                        <label class="button button--small card-figcaption-button compare" for="compare-{{id}}">
                          <span class="custom-checkbox"><input type="checkbox" name="products[]" value="{{id}}" id="compare-{{id}}" data-compare-id="{{id}}"></span>
                          <span class="compare-text">{{lang 'products.compare'}}</span>
                        </label>
                    {{/if}}
            </div>
            {{/or}}
        </div>
    </div>
</article>

components/products/list-item:
{{#if settings.data_tag_enabled}}
    <article class="listItem"
             data-event-type="{{event}}"
             data-entity-id="{{id}}"
             data-position="{{position}}"
             data-name="{{name}}"
             data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
             data-product-brand="{{brand.name}}"
             data-product-price="{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}"
    >
{{else}}
    <article class="listItem">
{{/if}}
    <figure class="listItem-figure">
        <a href="{{url}}"
           class="listItem-figure__link"
           aria-label="{{name}},{{> components/products/product-aria-label}}"
           {{#if settings.data_tag_enabled}}
           data-event-type="product-click"
           {{/if}}
        >
        {{#if stock_level '===' 0}}
            {{> components/products/product-badge
                badge-type='sold-out'
                badge_view=theme_settings.product_sold_out_badges
                badge_label=theme_settings.pdp_sold_out_label
                page_builder_key="pdp_sold_out_label"
                default_translation=(lang "page_builder.pdp_sold_out_label")
            }}
        {{else}}
            {{#or price.sale_price_with_tax.value price.sale_price_without_tax.value}}
                {{> components/products/product-badge
                    badge-type='sale'
                    badge_view=theme_settings.product_sale_badges
                    badge_label=theme_settings.pdp_sale_badge_label
                    page_builder_key="pdp_sale_badge_label"
                    default_translation=(lang "page_builder.pdp_sale_badge_label")
                }}
            {{/or}}
        {{/if}}
        {{> components/common/responsive-img
            image=image
            class="listItem-image"
            fallback_size=theme_settings.productgallery_size
            lazyload=theme_settings.lazyload_mode
            default_image=theme_settings.default_image_product
        }}
        </a>
        {{#unless hide_product_quick_view}}
            {{#if theme_settings.show_product_quick_view}}
                <div class="listItem-figureBody">
                    <button type="button" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small listItem-button quickview" data-product-id="{{id}}">{{lang 'products.quick_view'}}</button>
                </div>
            {{/if}}
        {{/unless}}
    </figure>
    <div class="listItem-body">
        <div class="listItem-content">
            <div class="listItem-details">
                {{#if show_rating}}
                    <p class="listItem-rating">{{> components/products/ratings rating_target=name rating=rating}}</p>
                {{/if}}
                {{#if brand.name}}
                <p class="listItem-brand">{{brand.name}}</p>
                {{/if}}
                <h4 class="listItem-title">
                    <a href="{{url}}"
                       aria-label="{{name}},{{> components/products/product-aria-label}}"
                       {{#if settings.data_tag_enabled}}data-event-type="product-click"{{/if}}
                    >
                        {{name}}
                    </a>
                </h4>
                {{#if summary}}
                    <p>{{{summary}}}</p>
                {{/if}}
            </div>
            <div class="listItem-actions">
                {{#or customer (if theme_settings.restrict_to_login '!==' true)}}
                    {{#if price}}
                        <div class="listItem-price">{{> components/products/price price=price}}</div>
                    {{/if}}
                    {{#if show_cart_action}}
                        {{#if has_options}}
                            <a href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--small" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                        {{/if}}
                        {{#if pre_order}}
                            <a href="{{pre_order_add_to_cart_url}}" data-event-type="product-click" class="button button--primary">{{lang 'products.pre_order'}}</a>
                        {{/if}}
                        {{#if add_to_cart_url }}
                            <a href="{{add_to_cart_url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}} class="button button--primary">{{lang 'products.add_to_cart'}}</a>
